function pickRandomItem(itemList=[]){if(itemList?.constructor!==Array){throw new Error("Invalid itemList")}return itemList[Math.floor(Math.random()*itemList.length)]}export default function lootBox(tierList={}){if(tierList?.constructor!==Object){throw new Error("Invalid tierList")}const rate=Math.random();let currentRate=0;for(const[key,tier]of Object.entries(tierList)){if(tier.rate?.constructor!==Number){throw new Error(`Invalid ${key} rate field`)}currentRate+=tier.rate;if(currentRate>rate){return{tier:key,item:pickRandomItem(tier.items)}}}}